INSERT INTO `t_nimble_entp` VALUES ('08c160c4-ebac-4eca-859e-2c2007af37f0', '372ba975-69c2-49ba-bcba-12d4a5a69431', 'F3102073', '上海嘉品蔬菜种植专业合作社', '103', '108', '102', NULL, '崔轶雄', NULL, '', NULL, NULL, '上海市', '上海市', '嘉定区', '华亭镇联华村788号1幢101室', 31.486099, 121.294414, NULL, '933101145852339921', '', NULL, NULL, NULL, '<p><span style=\"color:rgb(51, 51, 51); font-size:10pt\">上海嘉品蔬菜种植专业合作社成立于</span><span style=\"color:rgb(51, 51, 51); font-family:arial,sans-serif; font-size:10pt\">2011</span><span style=\"color:rgb(51, 51, 51); font-size:10pt\">年，是一家以可持续发展为理念，以农业为主导，集规模化、设施化、产业化于一体的新型都市农业模式经营的企业。</span></p>\n\n<p><span style=\"color:rgb(51, 51, 51); font-size:10pt\">合作社基地主要位于闵行区浦江镇、嘉定区华亭镇，占地面积</span><span style=\"color:rgb(51, 51, 51); font-family:arial,sans-serif; font-size:10pt\">1500</span><span style=\"color:rgb(51, 51, 51); font-size:10pt\">亩，同时也是上海市政府菜篮子工程基地。我们拥有</span><span style=\"color:rgb(51, 51, 51); font-family:arial,sans-serif; font-size:10pt\">20</span><span style=\"color:rgb(51, 51, 51); font-size:10pt\">年种植经验，园区种植了</span><span style=\"color:rgb(51, 51, 51); font-family:arial,sans-serif; font-size:10pt\">140</span><span style=\"color:rgb(51, 51, 51); font-size:10pt\">余种蔬菜、水果和芳香植物，均采用绿色无公害种植方式，自产自营，确保质量。严格按照良好农业规范（</span><span style=\"color:rgb(51, 51, 51); font-family:arial,sans-serif; font-size:10pt\">GAP</span><span style=\"color:rgb(51, 51, 51); font-size:10pt\">）生产操作，不使用除草剂和生长激素，每年通过最具权威机构认证，把提供优质绿色商品作为我们的责任。</span></p>', NULL, NULL, '配送追溯管理系统', 'afcdc909-d667-4dda-a1f2-7bcdc79bcb9d', NULL, NULL, NULL, '0', '2018-06-09 14:10:34', 'anting', '2018-06-09 14:10:34', 'anting', '', '', 'zsy_supermarket_system', '2030-12-20', '', '2018-03-06', '102', NULL, '061800493P', '101', '', NULL);
