INSERT INTO `t_nimble_entp` VALUES ('0d241dd2-11b4-44d8-b3e8-dc703b7db1c3', '648f3093-d2ee-4cf5-b0df-e9e369ac352d', 'F2200025', '吉林省长春皓月清真肉业股份有限公司', '102', '105', '102', '', '皓月集团', '徐丽敏', '02156385505', '', 'china', '吉林省', '长春市', '绿园区', '皓月大路11111号', 43.899801, 125.162293, '', 'JY12201060038492', '丛连彪', '2016-11-09', 50000000.0000, '', '<p>&nbsp; &nbsp; &nbsp; &nbsp; <strong><span style=\"font-size:18px\"><span style=\"font-family:楷体_gb2312\">吉林省长春皓月清真肉业股份有限公司是以肉牛屠宰深加工为主导产业的民营股份制企业，坐落在长春市西部，是<span style=\"color:#FF0000\">首批国家级现代农业产业化重点龙头企业</span>，<span style=\"color:#FF0000\">是吉林省农产品加工产业重点支柱企业。</span><br />\n&nbsp; &nbsp; &nbsp; &nbsp; 皓月始建于1998年1月，2000年元月正式投产，现有员工5500人。多年来，公司紧紧围绕主业，充分发挥国家政策优势、吉林地域优势和清真特色优势，深化循环经济理念，实施产业一体化战略，进行相关多元化发展。横跨畜牧养殖业、食品加工业、饲料加工业、制革业、生化制品业、有机肥加工业等6大产业，形成了良种繁育、胚胎移植、肉牛饲养、饲料加工、屠宰加工、熟食加工、皮革加工、生物制品加工、有机肥生产加工、市场流通、职业教育和旅游观光等12个相关联的生态农牧产业集群。现生产能力为：年屠宰肉牛100万头、熟食制品10万吨、饲料加工60万吨、生化制品1200吨、皮革加工50万标张。<br />\n&nbsp; &nbsp; &nbsp; &nbsp; 皓月视产品质量为企业的生命，秉承&ldquo;99+1=0&rdquo;的质量理念，从&ldquo;牛源品种、养殖饲料、肉牛检疫、生产加工、全程冷链&rdquo;等方面，严格执行&ldquo;从农场到餐桌&rdquo;全过程质量控制。2003年7月在国内肉牛行业首家通过<span style=\"color:#FF0000\">HACCP</span>国际食品安全体系认证，先后通过了<span style=\"color:#FF0000\">ISO9001</span>质量管理体系认证、<span style=\"color:#FF0000\">ISO14001</span>环境管理体系认证和<span style=\"color:#FF0000\">ISO22000</span>食品安全管理体系认证以及沙特、约旦、埃及、阿联酋、马来西亚和俄罗斯等国家进口食品认证注册，并在全国同行业中率先采用胶体金检测卡，对牛源实施违禁药物百分之百检测，确保食品安全。2008年1月，以皓月分割标准为版本的<span style=\"color:#FF0000\">《鲜、冻分割牛肉》</span>标准和<span style=\"color:#FF0000\">《鲜、冻四分体牛肉》</span>标准被上升为国家标准，实现了&ldquo;<span style=\"color:#FF0000\">一流企业出标准</span>&rdquo;的目标，对国内肉牛的分割标准规范、产品质量保证起到积极促进作用。<br />\n&nbsp; &nbsp; &nbsp; &nbsp; 皓月凭借产品质量及品牌优势，积极拓展营销网络。在国内形成以东三省为根据地，以京津唐地区、长江三角洲、珠江三角洲为重点的营销网络；在国际市场上形成了以阿联酋为中心的中东市场，以马来西亚为中心的东南亚市场，以俄罗斯为中心的东欧市场的三大格局，累计产品出口20个国家和地区。是中国乃至亚洲地区最大的肉牛加工企业，也是中国最大的优质牛肉出口基地。<br />\n&nbsp; &nbsp; &nbsp; &nbsp; 皓月凭借良好的基础，用工业化的思维谋划现代化农业的大发展。在保证企业自身持续发展的同时，带动百万农业大军，在产业化的道路上高歌猛进，为促进农业产业结构调整，推动农业经济发展，增加农民收入，破解&ldquo;三农&rdquo;问题，建设社会主义新农村，做出了巨大贡献。<br />\n&nbsp; &nbsp; &nbsp; &nbsp; <span style=\"color:#FF0000\">皓月在全国同行业中产业规模第一、品牌影响第一、出口创汇第一、发展潜力第一、社会效益第一。</span><br />\n&nbsp; &nbsp; &nbsp; &nbsp; 皓月在&ldquo;创新经营，永争第一&rdquo;的企业精神指引下，坚持&ldquo;绿色生态，循环再生&rdquo;的品牌定位，以&ldquo;产业报国，奉献社会，提升人类生活品质&rdquo;为使命，奉行&ldquo;诚信、自强、责任、共赢&rdquo;的价值观，为实现&ldquo;<span style=\"color:#FF0000\">把皓月办成我们国家在世界上肉牛羊产业的一面旗帜</span>&rdquo;的伟大愿景而奋斗</span></span></strong></p>\n\n<p>&nbsp;</p>', '', NULL, '配送追溯管理系统', 'afcdc909-d667-4dda-a1f2-7bcdc79bcb9d', NULL, NULL, NULL, '0', '2018-06-08 10:57:56', 'meilanhu', '2018-06-08 10:57:56', 'meilanhu', '长春皓月', '', 'zsy_supermarket_system', NULL, NULL, NULL, '102', NULL, '061700260V', '101', 'e3ec7026-2048-45b3-8af8-8823d5c9c4c0', NULL);
