INSERT INTO `t_nimble_entp` VALUES ('0efe8bb9-9351-473c-b2fd-98b3d6a343e9', '6c13eb3d-bee5-11e7-9a49-5254002f249f', 'F3101815', '上海苏食肉品销售有限公司', '999', '105', '102', '', '薛文忠', '薛文忠', '18951998691', '', 'china', '上海市', '上海市', '宝山区', '丰翔路1415号', 31.316104, 121.355593, '', '91310113133844127X', '', '2016-11-09', 10000000.0000, '', '<p>江苏省食品总公司组建于1953年，是江苏省肉类食品行业的主管机构，后改制为江苏省食品集团有限公司。 2012年，苏食集团与上海梅林正广和股份有限公司战略合作，苏食肉品板块正式纳入梅林股份（股票代码：600073）旗下。现梅林公司旗下拥有&ldquo;冠生园&rdquo;、&ldquo;大白兔&rdquo;、&ldquo;苏食&rdquo;三个中国驰名商标和&ldquo;梅林&rdquo;、<br />\n&ldquo;华佗&rdquo;、&ldquo;正广和&rdquo;、&ldquo;爱森&rdquo;等一批著名商标。<br />\n上海苏食肉品销售有限公司作为梅林旗下的一员，成立于1993年，注册资本1000万人民币，现拥有员工300多人，在上海地区已经建立了完善的产销网络，主要为生产加工企业提供生产原料；为大卖场、连锁超市、大专院校、餐饮业、机关企事业等伙食团体提供肉禽产品和配送服务。2013年始，我们在线上与1号店、天猫、京东等合作建立了苏食旗舰店；线下，我们开拓创新，逐步建立品牌直销中心苏食肉庄，多面的销售渠道，让&ldquo;苏食&rdquo;在增加销量的同时更突出了品牌效应。<br />\n&nbsp;上海公司秉承&ldquo;行合趋同 ，志逸四海&rdquo;的企业文化理念，在改革创新、与时俱进的经营管理下，销售额逐年递增，规模不断壮大，为上海市民及社会经济发展做出更大贡献。</p>', '', NULL, '配送追溯管理系统', 'afcdc909-d667-4dda-a1f2-7bcdc79bcb9d', NULL, NULL, NULL, '0', '2018-06-01 14:59:32', 'system', '2018-06-01 14:59:32', 'xuewenzhong', '上海苏食', '310113014', 'zsy_distribution_system', '2022-07-14', '', NULL, '102', NULL, '061700323F', '101', '', NULL);
