INSERT INTO `t_nimble_entp` VALUES ('0f57f60c-70ef-48fb-9bf4-14e78b34fb84', '6c1fc334-bee5-11e7-9a49-5254002f249f', 'F4600002', '海南昌之茂食品有限公司', '104', '105', '102', NULL, '叶声飞', NULL, '13818026118', NULL, NULL, '海南省', '海口市', '美兰区', '桂林洋经济开发区', 19.988828, 110.474565, NULL, '91460000735817900X', '', NULL, NULL, NULL, '<p style=\"text-align: center;\"><strong>《企业简介》</strong></p>\n\n<p>海南昌之茂食品有限公司始建于1998年，是一家立足海岛生态资源，产品面向全国的食品专业生产企业，产品以品质优良、口味丰富、包装精美等特点长期畅销于国内各大小型商超。</p>\n\n<p>公司坐落于美丽富饶的海南岛，其地处常年气候温和，四时常花，长夏无冬，是享誉世界的旅游城市，得益于当地得天独厚的地域环境和海洋优势，多年来公司不断开发以优质海产品、果蔬制品、酱腌菜、黄金灯笼椒等四大系列为主的海南特色产品及旅游休闲食品。</p>\n\n<p>作为海南省的大型食品生产企业，本企业凭借专业的食品研发团队与先进的生产设备，携手六百多名员工，致力于打造味觉上的创新体验，以卓越的生产成就，不断获得广大客户与社会各界的良好口碑。</p>', NULL, NULL, '配送追溯管理系统', 'afcdc909-d667-4dda-a1f2-7bcdc79bcb9d', NULL, NULL, NULL, '0', '2017-12-03 12:22:49', '13918051642', '2017-12-03 12:22:49', '13918051642', '', '', 'zsy_supermarket_system', NULL, NULL, '2017-11-27', '102', NULL, NULL, '101', NULL, NULL);
