INSERT INTO `t_nimble_entp` VALUES ('10c4eb6d-4b06-4d88-8557-c8cb8b06ea92', '6c1fc334-bee5-11e7-9a49-5254002f249f', 'F3100441', '上海崇明生态农业销售有限公司', '103', '105', '102', NULL, '袁一雷', NULL, '', NULL, NULL, '上海市', '上海市', '崇明县', '崇明区堡镇北路227号', 31.528601, 121.569100, NULL, '91310230MA1JX5X345', '', NULL, NULL, NULL, '<p>上海崇明生态农业发展有限公司是崇明最大的国有农业企业，注册资金为9000万元人民币，由崇明县国有资产监督管理委员会出资成立。2013年2月，县委、县政府为整合崇明岛丰富的生态农业资源，打造现代、高效、生态的农业体系，提升崇明生态农业产业能级，促进崇明生态农产品营销体系建设，最终达到我县农民增收、农业增效、农村可持续发展的目标而投资成立。</p>\n\n<p>现在供应盒马18个品项</p>\n\n<p>基地规模3000亩，现有员工30人，包装车间2、冷藏库3、冷冻库2个、配送车辆25台、检测室齐全。种植品种：叶菜类、茄果类、根茎类。</p>\n\n<p>依照全岛规划，公司将服务于崇明农产品生产与市区消费市场，以市场需求为导向，整合优化、统筹协调崇明地产农产品的基地生产、物流配送、营销经营等社会零散资源；开发具有行业领先品牌的绿色、新鲜、安全的生态农产品系列；承担将崇明农产品进市区、促进农民增收的社会责任，构建政府扶持农业产业化发展投入机制的国有控股产销平台，逐步形成符合崇明生态岛发展的订单农业模式，从而为崇明本地农业、农民及上海市民创造价值。</p>\n\n<p>公司的发展目标为：</p>\n\n<p>1、建立崇明生态农业公共物流平台，通过建立国际领先的物流服务标准，实现农副产品在物流环节的安全的、先进的和透明的服务和管理。</p>\n\n<p>2、&ldquo;崇明岛生态农产品&rdquo;品牌成为上海市民消费鲜活农产品（包含新鲜蔬菜、水果，鲜活水产品，活的畜禽、家畜，活的畜禽，新鲜的肉、蛋、奶）的首选品牌。</p>', NULL, NULL, '配送追溯管理系统', 'afcdc909-d667-4dda-a1f2-7bcdc79bcb9d', NULL, NULL, NULL, '0', '2018-01-11 09:58:22', '13918904724', '2018-01-11 09:58:22', '13918904724', '崇明生态公司', '', 'zsy_supermarket_system', NULL, '', '2018-01-08', '102', NULL, NULL, '101', NULL, NULL);
INSERT INTO `t_nimble_entp` VALUES ('10cee9c5-9688-4eca-80df-b7f0ec69b2a6', '6ba4ac34-bee5-11e7-9a49-5254002f249f', 'F3100426', '上海恒孚农业科技有限公司', '103', '105', '102', NULL, '许珍妮', NULL, '', NULL, NULL, '上海市', '上海市', '闵行区', '纪鹤路596弄88号', 31.252503, 121.258317, NULL, '913101125852693462', '', NULL, NULL, NULL, '<p>&nbsp; &nbsp; 上海恒孚农业科技有限公司成立于2011年，专注于鲜切蔬菜的加工和新鲜水果的分装，为商超、游轮、餐饮业、企事业食堂、学校食堂等客户的蔬果产品需求提供个性化的服务。公司针对鲜切蔬果产品新设了2800多平米的冷链加工区及仓库，并有自己的冷链配送车辆，做到实时监控，来确保鲜切蔬果的品质。</p>', NULL, NULL, '配送追溯管理系统', 'afcdc909-d667-4dda-a1f2-7bcdc79bcb9d', NULL, NULL, NULL, '0', '2018-01-07 08:41:22', 'liumeng', '2018-06-28 14:59:33', '13759979840', '', '', 'zsy_supermarket_system', NULL, '', '2018-01-03', '102', NULL, NULL, '101', NULL, NULL);
