INSERT INTO `t_nimble_entp` VALUES ('12aa4ff2-c697-4905-b653-c6d5801596ae', '0', 'F3100159', '上海润峰食品有限公司', '102', '105', '101', NULL, '王翠翠', NULL, '', NULL, NULL, '上海市', '上海市', '宝山区', '南大路164号南部3幢102室', 31.305532, 121.407207, NULL, '91310113076443131A', '', NULL, NULL, NULL, '<p><strong><span style=\"font-size:14px\"><span style=\"font-family:仿宋_gb2312\">我公司主要经营牛羊肉批发及生鲜、禽类肉品的公司，主要包括高档部位肉、冷却保鲜肉、肥牛、肥羊、牛羊副产品、熟食制品等300多个品种，在本行业从事多年，多家大润发设有专柜。建立了以肉食品加工为主体、以绿色饲料加工、畜牧产品研制开发、草原生态建设为基础的肉牛集约化和规模化的产业链条，成为国际化、专业化的现代肉牛产业。</span></span></strong></p>', NULL, NULL, '配送追溯管理系统', 'afcdc909-d667-4dda-a1f2-7bcdc79bcb9d', NULL, NULL, NULL, '0', '2017-11-27 10:15:47', 'panhaoran', '2018-05-14 17:13:19', 'runfengshipin', '', '', 'zsy_distribution_system', NULL, NULL, '2017-11-27', '102', NULL, '0618005023', '101', '', NULL);
