INSERT INTO `t_nimble_entp` VALUES ('15001088-2884-4ec4-8d33-4846168ad76b', 'a7c0e0e3-47ee-4895-928f-b8c7ae1df9bd', 'F3102472', '上海才宝水产苗种养殖专业合作社', '', '105', '102', NULL, '周冲3', NULL, '13601623315', NULL, NULL, '上海市', '上海市', '金山区', '吕巷镇颜圩村寒圩5071号', 30.838614, 121.278628, NULL, '93310116066002412Y', '', NULL, NULL, NULL, '<p>上海才宝水产苗种养殖专业合作社是一家主营水产养殖与冰鲜水产收购的企业。依托上海为中心，福射全国。在养殖水产与冰鲜水产品行业深耕市场渠道和水产品牌推广5余年，积累了丰富的市场经验。目前，主要业务为提供优质的冰鲜海鲜鱼，养殖南美白虾。不断与国内各大型商超紧密合作，继续做好冰鲜水产。</p>\n\n<p>才宝员工奉行&ldquo;务实、固结、开拓、进取&rdquo;的精神，以人为本，视质量为生命，不断提供优质的冰鲜海产品，服务好每一位顾客。</p>', NULL, NULL, '配送追溯管理系统', 'afcdc909-d667-4dda-a1f2-7bcdc79bcb9d', NULL, NULL, NULL, '0', '2018-06-19 10:03:18', 'sanlin', '2018-06-19 10:03:18', 'sanlin', '', '310110021', 'zsy_supermarket_system', '2070-12-26', '', NULL, '102', NULL, '061800758H', '101', '', NULL);
