INSERT INTO `t_nimble_entp` VALUES ('151536bc-0a7c-4650-9389-172c94670b20', '6c1fc334-bee5-11e7-9a49-5254002f249f', '13499999', '安徽省和县济洪蔬菜有限公司', '103', '105', '102', '', '济洪蔬菜', '陆济洪', '13901893111', '', 'china', '上海市', '上海市', '青浦区', '华隆路1777号e通世界D座1003室', 31.205921, 121.269653, '', '91340523779058969P', '', '2016-11-09', 50000000.0000, '', '<p><span style=\"font-size:14px\">自1983年陆济洪先生进入蔬菜行业起，便致力于如何将优质、新鲜、安全的蔬菜送到千家万户的餐桌上。这成为济洪蔬菜人一直坚持追求的梦想，并为之努力至今。如今济洪蔬菜已成长为成熟的集蔬菜种植、加工、配送为一体的蔬菜全产业链企业，在全国各主要大中城市都设有配送网点和办事处，2015年营业额达到10亿元以上。</span></p>\n\n<p><span style=\"font-size:14px\">公司拥有5个自有蔬菜基地，在全国有二十多家合作农场和订单种植基地，在上海江桥、华新等地建有5000多平米的现代化蔬菜加工配送中心，拥有30万级净化厂房、冷藏库和现代化检测室，通过了HACCP和ISO9001体系认证。</span></p>\n\n<p><span style=\"font-size:14px\">&ldquo;济洪&rdquo;牌蔬菜连续多年被评为上海市名牌产品、上海市著名商标，以及上海市食用农产品十大畅销品牌等。</span></p>\n\n<p><span style=\"font-size:14px\">1983年陆济洪先生在胶州路市场开始创业，1991年成为华亭宾馆（五星级）的供应商，陆续配送上海三十余家高档酒店蔬菜。1997年成为麦德龙的战略合作伙伴，是麦德龙中国区的第7个供应商，1999年成为大润发主力供应商，2000年后，陆续成为家乐福、沃尔玛、乐购、乐天玛特、欧尚、华润万家、世纪联华、物美等大型连锁超市蔬菜供应商，2005年在安徽和县建立第一个自有农场，之后宁波、嘉兴、武汉、崇明自有基地陆续建立，2006年首次被评为上海市名牌产品，2007年起与麦咨达建立战略合作关系，在他们的指导下，自有基地按照良好农业操作规范（gap）进行种植、生产。基地产品实现从田头到餐桌全程可追溯。</span></p>\n\n<p><span style=\"font-size:14px\">2009年上海青浦净菜厂自建，同时建成可进行农残快速检测的理化实验室，2010年成为上海世博会指定后勤蔬菜供应商，加工中心通过了ISO 9001、HACCP认证，2011年与西郊国际达成战略合作协议，2012年邀请时任安徽省委书记张宝顺视察和县基地，2012年成为上海高校后勤食堂蔬菜供应商，2015年被评为上海市著名商标，2015年成为上海迪士尼的净菜供应商。</span></p>', '', NULL, '配送追溯管理系统', 'afcdc909-d667-4dda-a1f2-7bcdc79bcb9d', NULL, NULL, NULL, '0', '2017-12-01 15:21:59', 'liumeng1', '2017-12-01 15:21:59', 'liumeng1', '', '', 'zsy_supermarket_system', NULL, NULL, NULL, '102', NULL, NULL, '101', NULL, NULL);
