INSERT INTO `t_nimble_entp` VALUES ('155064ef-6147-4693-8137-f3bacad9ce2f', '6c1fc334-bee5-11e7-9a49-5254002f249f', '13199981', '上海海天下食品有限公司', '104', '105', '102', '', '黄国林', '黄玉树', '18616658238', '', 'china', '上海市', '上海市', '嘉定区', '翔江路2000号第4幢', 31.283207, 121.282179, '', '913101147752076809', '', '2016-11-09', 50000000.0000, '', '<p>&nbsp; &nbsp; &nbsp;海天下自1998年由创始人黄玉树先生创立于中国上海，自品牌创始以来，我们始终向大众展现着一路领鲜的海洋水产精髓，并为广大消费者带来超级新鲜的美味享受！<br />\n&nbsp;&nbsp;&nbsp;&nbsp;公司专注于全球水产采购加工，现代商超渠道、大型餐团连锁集团的水产生鲜供应链平台服务。公司于上海市嘉定区建设近千亩的现代农渔业基地、万吨级冷库，公司旗下第三方冷链物流&mdash;&mdash;上海快行天下冷链物流有限公司承接全程冷链配送，水产生鲜冷链直送能力覆盖全国。&nbsp;<br />\n&nbsp;&nbsp;&nbsp;&nbsp;&ldquo;海天下&rdquo;品牌荣获上海名牌、上海市著名商标等荣誉称号，与线下大润发、麦德龙、欧尚、沃尔玛、家乐福、乐购、永辉、华润、盒马鲜生、城市超市等国内外著名连锁超市，以及线上京东、天猫、易果生鲜、每日优鲜、东方CJ等电商平台建立良好的合作关系。<br />\n&nbsp;&nbsp;&nbsp;&nbsp;全球水产，1路领鲜是海天下人不断追求的目标，公司严格执行HACCP国际食品标准体系，运用现代化的电子商务物联网追溯系统，将海洋水产捕捞养殖、生产加工、冷链仓储配送等一系列产品信息做到全程追溯，尤其在现代商超的水产现场加工，精湛的现场贩售技巧更是培育了一支专业的优秀推广团队，核心竞争优势日趋明显。丰富的全球水产资源，源源出新的优质产品和对产品质量的高要求、高标准使得海天下深入人心，从而必将成为水产生鲜行业1路领先的品牌。&nbsp;</p>', '', NULL, '配送追溯管理系统', 'afcdc909-d667-4dda-a1f2-7bcdc79bcb9d', NULL, NULL, NULL, '0', '2017-12-15 14:34:31', 'liumeng1', '2017-12-15 14:34:31', 'liumeng1', '', '', 'zsy_supermarket_system', NULL, NULL, '2017-11-27', '102', NULL, NULL, '101', NULL, NULL);
