INSERT INTO `t_nimble_entp` VALUES ('1627fa7d-7503-4199-a1b9-5f368471c1de', '4e3b4539-cd11-4097-a066-0cb276d955e9', 'F3101218', '佳农食品（上海）有限公司', '999', '999', '102', NULL, '石毅', NULL, '15901900957', NULL, NULL, '上海市', '上海市', '浦东新区', '上川路1499-1号2幢', 31.284016, 121.669988, NULL, '91310115086200985E', '刘自杰', NULL, NULL, NULL, '<p>佳农食品控股(集团)股份有限公司</p>\n\n<p>GOODFARMER FOODS HOLDING ( GROUP ) CO., LTD.</p>\n\n<p>佳农业务始自于2002年，立足中国上海，致力于成为中国第一、世界一流的品牌果蔬食品配送服务商。秉承这一理念，佳农公司积极进取，不断地推动业务的发展与壮大，目前公司业务涵盖果蔬出口、进口、批发以及冷链 物流配送四大板块。</p>\n\n<p>&nbsp;</p>\n\n<p>为了更好地推动品牌化建设的步伐，佳农整合优势资源，构建起以产品和客户为核心的导向服务。产品包含香蕉、菠萝、火龙果、苹果、大蒜、生姜等进出口果蔬，以其优异的质量和稳定的供应，获得众多客户的推崇和喜爱。</p>\n\n<p>&nbsp;</p>\n\n<p>我们组建了以客户为核心的专业化营销团队，推动佳农在全球广阔的市场上拓宽渠道，依托国内外销售网络，向众多大客户源源不断提供国内外优质农产品。</p>\n\n<p>&nbsp;</p>\n\n<p>鉴于超市、连锁店、电商等品质要求较高的客户需求，我们专门成立了现代化的冷链物流和配送中心，并与华润万家、沃尔玛、百果园等达成战略合作。</p>\n\n<p>&nbsp;</p>\n\n<p>每一个佳农人正以更坚定的信念，团结一心，凝神聚力，秉承&ldquo;团队、诚信、专业、创新、共嬴&rdquo;的核心价值观，为佳农更美好的明天而努力！</p>', NULL, NULL, '配送追溯管理系统', 'afcdc909-d667-4dda-a1f2-7bcdc79bcb9d', NULL, NULL, NULL, '0', '2018-05-21 15:56:37', 'songjiang', '2018-05-21 15:56:37', 'songjiang', '', '310115369', 'zsy_supermarket_system', NULL, '', '2018-01-17', '102', NULL, '061800422V', '101', '', NULL);
