INSERT INTO `t_nimble_entp` VALUES ('1a16889b-20f6-44f5-8b1e-a8ef551f5655', '0', 'F3103874', '上海枫北水稻种植专业合作社', '105', '105', '101', NULL, '包永华', NULL, '13524675202', NULL, NULL, '上海市', '上海市', '金山区', '金 山区枫泾镇卫星村毛家7组1032号', 30.904444, 121.095449, NULL, '93310116692941919H', '', NULL, NULL, NULL, '<p><strong>企业简介</strong></p>\n\n<p>&nbsp;&nbsp;&nbsp; 上海枫北水稻种植专业合作社是一家农业专业合作社，于2009年8经国家相关部门批准注册成立。主营水稻种植，并还兼农业机械服务。经过几年来发展，合作社规模不断扩大，经济效益不断提高，社会效益不断提升。合作社目前已拥有大中型拖拉机2台，联合收割机2台，插秧机6台，育秧流水线1套，仓库1500平方米，水泥晒场2000平方米等。合作社法人代表包永华他先后获得了《上海市新型职业农民证书》，市级示范合作社，2012年获得国务院颁发的全国种粮大户荣誉称号。</p>\n\n<p><strong>上海枫北水稻种植专业合作社本着&ldquo;客户第一，诚信至上&rdquo;的原则，与多家企业建立了长期的合作关系。</strong></p>\n\n<p><strong>合作社坚持走生态农业创新发展之路，依靠当地的自然环境资源，有优势开展&ldquo;三品&rdquo;[无公害食品、绿色食品、有机食品]生产。合作社迅速发展，产品得到广大消费者的青睐和认可。</strong></p>\n\n<p><strong><img alt=\"\" src=\"http://saasimage.zhuisuyun.net/zsy-saas/static/20181016/4439400dfee94e508a6da76608b00db1.jpg\" style=\"height:717px; width:538px\" /><img alt=\"\" src=\"http://saasimage.zhuisuyun.net/zsy-saas/static/20181016/9e74498f42fb4f228f48dba0d8410420.jpg\" style=\"height:717px; width:538px\" /><img alt=\"\" src=\"http://saasimage.zhuisuyun.net/zsy-saas/static/20181016/adeb0f6d88f3490db7aee2c2e8932424.jpg\" style=\"height:717px; width:538px\" /><img alt=\"\" src=\"http://saasimage.zhuisuyun.net/zsy-saas/static/20181016/315d479ba84f4972b25e50ac5fca6caf.jpg\" style=\"height:717px; width:538px\" /></strong></p>\n', NULL, NULL, '种植追溯管理系统', 'afcdc909-d667-4dda-a1f2-7bcdc79bcb9d', NULL, NULL, NULL, '0', '2018-10-16 10:51:16', 'liguochao', '2018-10-16 10:51:16', 'liguochao', '枫北水稻', '', 'zsy_planting_system', '2080-01-01', '7a958ba6-6030-4ce3-9ee2-c0f87de14c64', NULL, '102', NULL, '061801961J', '101', '', NULL);
